  `sort_order` tinyint(3) unsigned NOT NULL DEFAULT '1',
  `supplier_id` int(10) unsigned NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`,`supplier_id`),
  KEY `parent_id` (`parent_id`)
) ENGINE=MyISAM AUTO_INCREMENT=817 DEFAULT CHARSET=utf8");
